mapper.select:
select p.user_id, p.type_id, p.remark, p.grade, p.tuan_flag, p.cu_flag, p.ding_flag, p.pai_flag, p.main_image_id, p.address, p.sales_count, p.phone, p.name, p.created_date from sys_business_man p where 1=1
and p.name LIKE CONCAT(CONCAT('%', #{pd.name}),'%')
and p.created_date >= #{pd.lastLoginStart}
and p.created_date <= #{pd.lastLoginEnd}
and p.ding_flag=#{pd.ding_flag}
