mapper.insert:
insert into bus_favorites (id,
user_id, type,
product_id, man_id, create_time
)
values
(#{id,jdbcType=BIGINT}, #{userId,jdbcType=BIGINT},
#{type,jdbcType=VARCHAR},
#{productId,jdbcType=BIGINT},
#{manId,jdbcType=BIGINT}, #{createTime,jdbcType=TIMESTAMP}
)

mapper.select:
select

from bus_favorites
where user_id = #{userId,jdbcType=BIGINT}
and
product_id = #{productId,jdbcType=BIGINT}
and type='2'

select

from bus_favorites
where user_id = #{userId,jdbcType=BIGINT}
and man_id
= #{manId,jdbcType=BIGINT}
and type='1'
