mapper.select:
select o.id, o.status, o.price, o.created_date, a.username, p.name from bus_order o,bus_product p,sys_app_user a where o.product_id = p.id AND o.user_id = a.user_id
and a.USERNAME LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%')
and p.name LIKE CONCAT(CONCAT('%', #{pd.PRODUCTNAME}),'%')
and o.CREATED_DATE >= #{pd.lastLoginStart}
and o.CREATED_DATE <= #{pd.lastLoginEnd}
and o.STATUS=#{pd.STATUS}
