mapper.insert:
insert into bus_product (id, name, detail, 
  old_price, new_price, recommend, 
  top_grade, user_id, type_id, 
  sales_count, info, notice
  )
values (#{id,jdbcType=BIGINT}, #{name,jdbcType=VARCHAR}, #{detail,jdbcType=VARCHAR}, 
  #{oldPrice,jdbcType=DECIMAL}, #{newPrice,jdbcType=DECIMAL}, #{recommend,jdbcType=BIT}, 
  #{topGrade,jdbcType=DOUBLE}, #{userId,jdbcType=BIGINT}, #{typeId,jdbcType=BIGINT}, 
  #{salesCount,jdbcType=BIGINT}, #{info,jdbcType=VARCHAR}, #{notice,jdbcType=VARCHAR}
  )

mapper.select:
select p.id, p.name, p.detail, p.old_price, p.new_price, p.recommend, p.top_grade, p.user_id, p.type_id, p.sales_count, p.info, p.notice, p.created_date from bus_product p where 1=1
and p.name LIKE CONCAT(CONCAT('%', #{pd.productname}),'%')
and p.created_date >= #{pd.lastLoginStart}
and p.created_date <= #{pd.lastLoginEnd}
and o.status=#{pd.status}
