mapper.select:
select

from bus_shopping_cart
where product_id = #{productId,jdbcType=BIGINT}
and user_id =
#{userId,jdbcType=BIGINT}

select s.id, s.status, s.count, s.created_date, a.username, p.name from bus_shopping_cart s,bus_product p,sys_app_user a where s.product_id = p.id AND s.user_id = a.user_id
and a.USERNAME LIKE CONCAT(CONCAT('%', #{pd.username}),'%')
and p.name LIKE CONCAT(CONCAT('%', #{pd.productname}),'%')
and s.CREATED_DATE >= #{pd.lastLoginStart}
and s.CREATED_DATE <= #{pd.lastLoginEnd}
and s.status=#{pd.status}

select o.id, o.status, o.price, o.created_date, a.username, p.name from bus_order o,bus_product p,sys_app_user a where o.product_id = p.id AND o.user_id = a.user_id
and a.USERNAME LIKE CONCAT(CONCAT('%', #{pd.username}),'%')
and p.name LIKE CONCAT(CONCAT('%', #{pd.productname}),'%')
and s.CREATED_DATE >= #{pd.lastLoginStart}
and s.CREATED_DATE <= #{pd.lastLoginEnd}
and s.status=#{pd.status}
